import { Component, OnInit } from '@angular/core';
import { Pelicula } from 'src/app/models/modelPeliculas';
import { DataService } from 'src/app/models/modelService';
import { DataServiceService } from 'src/app/services/data-service.service';

@Component({
  selector: 'app-cards',
  templateUrl: './cards.component.html',
  styleUrls: ['./cards.component.css']
})
export class CardsComponent implements OnInit {
  peliculas:Pelicula[]=[];
  constructor(private dataService: DataServiceService){}
  ngOnInit(): void {
    this.dataService.state$.subscribe(state=>{
      if(state.peliculas){
        this.peliculas=state.peliculas;
        console.log(this.peliculas)
      }
    })
  }
}
<div class="row">
    <div class="col-12 container-cards">
        <div class="card" style="background-color:cadetblue; border-color:darkblue;">
          <img class="card-img-top" src="holder.js/100x180/" alt="">
          <div class="card-body">
            <h4 class="card-title">Title</h4>
            <p class="card-text">Text</p>
          </div>
        </div>
    </div>
</div>
